        last_move = 0
    else:
        last_move = random.choice([1, 2, 3])

    while cakes > 0:
        if cakes == 1:
            return "Lost"
        move = player.move(cakes, last_move)
        if move == last_move:
            return "Invalid move"
        cakes -= move
