from collections import defaultdict, deque
from typing import List

# all class and function definitions in the code file, if any
class Solution:
    def alienOrder(self, words: List[str]) -> str:
        G = self.construct_graph(words)
        visited = defaultdict(int)  # 0 not visited, 1 visiting, 2 visited
        ret = deque()
        for u in G.keys():
            if visited[u] == 0:
                if not self.topo_dfs(G, u, visited, ret):
                    return ""

        return "".join(ret)

    def construct_graph(self, words):
        G = defaultdict(list)
        # need to initialize, consider test case ["z", "z"]
        for w in words:  # error
            for c in w:
                G[c]

        for i in range(len(words) - 1):  # compare word_i and word_{i+1}
            for c1, c2 in zip(words[i], words[i+1]):
                if c1 != c2:  # lexical order
                    G[c1].append(c2)
                    break  # need to break for lexical order

        return G

    def topo_dfs(self, G, u, visited, ret):
        """
        Topological sort
        G = defaultdict(list)
        visited = defaultdict(int)  # 0 not visited, 1 visiting, 2 visited

        pre-condition: u is not visited (0)
        """
        visited[u] = 1
        for nbr in G[u]:
            if visited[nbr] == 1:
                return False
            if visited[nbr] == 0:
                if not self.topo_dfs(G, nbr, visited, ret):
                    return False

        visited[u] = 2
        ret.appendleft(u)  # visit larger first
        return True

# main function
def main_solution(words: List[str]) -> str:
    # all input arguments of the main_solution function should be json serializable (no self-defined objects, functions, np.array, set, tuple, etc.)
    # if you need to invoke functions that require non-json serializable inputs, like those defined in the reference code file, you need to convert them (optional)
    solution = Solution()
    result = solution.alienOrder(words)
    # return, the returned value must be json serializable (no self-defined objects, functions, np.array, set, tuple, etc.)
    return result

