import random

# main function
def main_solution(R, C, M):
    """
    Solves the problem of placing mines in a grid of size R x C with M mines, ensuring that there is a path from the top-left corner to the bottom-right corner without stepping on a mine.

    Args:
    R (int): Number of rows in the grid.
    C (int): Number of columns in the grid.
    M (int): Number of mines to be placed in the grid.

    Returns:
    str: A string representation of the grid with 'c' marking the starting point, '.' marking safe cells, and '*' marking mines. If no valid configuration is possible, returns "Impossible".
    """
    blanks = R * C - M
    if blanks <= 0:
        return "Impossible"

    grid = [['*' for _ in range(C)] for _ in range(R)]

    if blanks == 1:
        grid[0][0] = 'c'
        return '\n'.join(''.join(row) for row in grid)

    if R == 1:
        for i in range(blanks):
            grid[0][i] = '.'
        grid[0][0] = 'c'
        return '\n'.join(''.join(row) for row in grid)

    if C == 1:
        for i in range(blanks):
            grid[i][0] = '.'
        grid[0][0] = 'c'
        return '\n'.join(''.join(row) for row in grid)

    if blanks in [2, 3, 5, 7]:
        return "Impossible"

    rows = 2
    columns = 2
    found_match = False

    while not found_match:
        leftover = blanks - rows * columns
        if leftover < 0 or columns > C:
            rows += 1
            columns = 2
        elif leftover == 0:
            found_match = True
        elif rows != R and columns != C:
            if leftover >= 2 and (leftover <= columns or leftover <= rows):
                found_match = True
            elif leftover < rows + columns and leftover >= 5:
                found_match = True
            else:
                columns += 1
        elif rows == R and (leftover <= rows and leftover > 1):
            found_match = True
        elif columns == C and (leftover <= columns and leftover > 1):
            found_match = True
        else:
            columns += 1
        if rows > R:
            break

    if not found_match:
        return "Impossible"

    for i in range(rows):
        for j in range(columns):
            grid[i][j] = '.'

    if leftover <= columns:
        for i in range(leftover):
            grid[rows][i] = '.'
    elif leftover <= rows:
        for i in range(leftover):
            grid[i][columns] = '.'
    else:
        extra = leftover - 2
        while C < extra:
            extra -= 1
        for i in range(extra):
            grid[rows][i] = '.'
        leftover -= extra
        for i in range(leftover):
            grid[i][columns] = '.'

    grid[0][0] = 'c'
    return '\n'.join(''.join(row) for row in grid)

