# all class and function definitions in the code file, if any
def count_letters(string):
    counts = {'odd': ''}
    i = 0

    while (i < len(string)):
        if (string[i] in counts):
            counts[string[i]] += 1
        else:
            counts[string[i]] = 1
        i += 1

    return counts

def can_make_palindrome(string, counts):
    odd = 0

    for k, v in counts.items():
        if (k != 'odd' and counts[k] & 1):
            odd += 1
            counts['odd'] = k
            if (odd > 1):
                break

    if (len(string) & 1):
        return odd == 1
    else:
        return odd == 0

def permute_palindromes(string):
    palindromes = []
    counts = count_letters(string)

    if (can_make_palindrome(string, counts)):
        initial = ""

        for k, v in counts.items():
            if (k != 'odd'):
                initial += (counts[k] // 2) * k

        permute(list(initial), 0, palindromes, counts['odd'])

    return palindromes

def swap(string, x, y):
    temp = string[x]
    string[x] = string[y]
    string[y] = temp

    return string

def permute(string_list, start, palindromes, separator=''):
    palindrome = "".join(string_list) + separator + "".join(string_list[::-1])
    palindromes.append(palindrome)

    i = start

    for i in range(start, len(string_list)):
        if (string_list[start] != string_list[i]):
            swap(string_list, start, i)
            permute(string_list, i+1, palindromes, separator)
            swap(string_list, start, i)

# main function
def main_solution(input_string):
    # Convert the input string to a JSON serializable format
    input_string = str(input_string)

    # Call the permute_palindromes function
    palindromes = permute_palindromes(input_string)

    # Convert the output to a JSON serializable format
    palindromes = [str(p) for p in palindromes]

    return palindromes

