class TreeNode:
    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

class Solution:
    def isSymmetrical(self, pRoot):
        def isSame(p, q):
            if p == None and q == None: return True
            if p == None or q == None: return False
            if p.val != q.val: return False
            return isSame(p.left, q.right) and isSame(p.right, q.left)

        return isSame(pRoot, pRoot)

# main function
def main_solution(tree_structure):
    # Convert the JSON serializable input to the original input variables
    def build_tree(node_list):
        if not node_list:
            return None
        nodes = [TreeNode(val) if val is not None else None for val in node_list]
        n = len(nodes)
        for i in range(n):
            if nodes[i] is not None:
                left_index = 2 * i + 1
                right_index = 2 * i + 2
                if left_index < n:
                    nodes[i].left = nodes[left_index]
                if right_index < n:
                    nodes[i].right = nodes[right_index]
        return nodes[0]

    pRoot = build_tree(tree_structure)

    # Invoke the function that requires non-JSON serializable inputs
    solution = Solution()
    result = solution.isSymmetrical(pRoot)

    # Convert the result to a JSON serializable output
