def isprime(n, i):
    if n == 1:
        return False
    if n == 2:
        return True

    if i > int(n**0.5)+1:
        return True
    if n%i == 0:
        return False
    if i == 2:
        i += 1
    else:
        i += 2

    return isprime(n,i)

def palincheck(word):
    if len(word) < 2: return True
    if word[0] != word[-1]: return False
    return palincheck(word[1:-1])

def find_palindromic_primes(start, end):
    palindromic_primes = []

    def ispalin(n, e):
        if n > e:
            return

        n_str = str(n)
        if palincheck(n_str) == True:
            if isprime(int(n_str), 2) == True:
                palindromic_primes.append(n_str)

        ispalin(int(n_str)+1, e)

    ispalin(start, end)
    return palindromic_primes

