from math import factorial

# all class and function definitions in the code file, if any
def f(x):
    return float(x)

def product(iterable):
    prod = 1
    for n in iterable:
        prod *= n
    return prod

def npr(n, r):
    assert 0 <= r <= n
    return product(range(n - r + 1, n + 1))

def ncr(n, r):
    assert 0 <= r <= n
    if r > n // 2:
        r = n - r
    return npr(n, r) // factorial(r)

def rolodex(n):
    inf = int(1e3)
    so = 0.0
    for r in range(n, inf):
        si = 0.0
        for i in range(n):
            p1 = f(ncr(n,i))
            p2 = f((-1)**i)
            p3 = (1.0 - f((i+1))/f(n))**f((r-1))
            si += p1*p2*p3
        so += f(r)*si 
    return so

# main function
