# all class and function definitions in the code file, if any
class TwoDMazeSolver:

    def __init__(self, maze):
        self.maze = maze

    @staticmethod
    def find_start(maze):
        for i in range(len(maze[0])):
            if maze[0][i] == 0:
                return 0, i
        return -1, -1

    @staticmethod
    def check_bounds(row, col, maze):
        if row < 0 or col < 0 or row >= len(maze) or col >= len(maze[0]):
            return False
        if maze[row][col] == 1 or maze[row][col] == 2:
            return False
        return True

    @staticmethod
    def get_surrounding(row, col):
        res = []
        for x, y in ((0, 1), (1, 0), (-1, 0), (0, -1)):
            res.append((row + x, col + y))
        return res

    @staticmethod
    def get_valid_moves(row, col, maze):
        if maze[row][col] == 1:
            return []
        surr = TwoDMazeSolver.get_surrounding(row, col)
        valid = [(x, y) for x, y in surr if TwoDMazeSolver.check_bounds(x, y, maze)]
        return valid

def bfs_solve(maze):
    row, col = TwoDMazeSolver.find_start(maze)
    q = queue.Queue()
    q.put((row, col))
    while not q.empty():
        r, c = q.get()
        maze[r][c] = 2
        if r == len(maze) - 1:
            return True
        surr = TwoDMazeSolver.get_surrounding(r, c)
        for nr, nc in surr:
            if TwoDMazeSolver.check_bounds(nr, nc, maze):
                q.put((nr, nc))
    return False

# main function
def main_solution(maze):
    # Convert the maze to a list of lists if it's not already
    if isinstance(maze, str):
        maze = [list(map(int, row.split(','))) for row in maze.split(';')]

    # Solve the maze using BFS
    solved = bfs_solve(maze)

    # Convert the maze back to a string representation
    maze_str = ';'.join(','.join(map(str, row)) for row in maze)

    # Return the solved maze and the result of the solution
    return {"solved_maze": maze_str, "solved": solved}

