import numpy as np

# Function to be solved -----------------------------------

def f(x):
    return np.exp(-x**2)

def romberg(col1):
    n = len(col1)
    for j in range(n - 1):
        temp_col = [0] * (n - 1 - j)
        for i in range(n - 1 - j):
            power = j + 1
            temp_col[i] = (4 ** power * col1[i + 1] - col1[i]) / (4 ** power - 1)
        col1[:n - 1 - j] = temp_col
    return col1[0]

def trapezio(f, a, b, h):                            
    n = int((b - a) / h)
    soma = 0
    for k in range(1, n):
        soma += f(a + k * h)
    return (h / 2) * (f(a) + 2 * soma + f(b))

