class Solution(object):
    def solveNQueens(self, n):
        """
        :type n: int
        :rtype: List[List[str]]
        """
        result = []

        def isVaild(j, set):
            for i in range(0, len(set)):
                if set[i] == j or abs(i - len(set)) == abs(set[i] - j):
                    return False
            return True

        def callback(i, set):
            if len(set) == n:
                temp = []
                for k in range(0, n):
                    row = '.' * set[k] + 'Q' + '.' * (n - set[k] - 1)
                    temp.append(row)
                result.append(temp)
                return

            for j in range(0, n):
                if isVaild(j, set):
                    set.append(j)
                    callback(i + 1, set)
                    set.pop()

        for i in range(0, n):
            callback(0, [i])

        return result

