# all class and function definitions in the code file, if any
def getSuccessorState(px, py, grid, x, y):
    if px < 0 or px > x-1 or py < 0 or py > y-1:
        raise ValueError("Invalid point given!")
    options = []
    currentElevation = grid[py][px]
    # West option
    if px > 0 and currentElevation > grid[py][px-1]:
        options.append((py, px-1))
    # East option
    if px < x-1 and currentElevation > grid[py][px+1]:
        options.append((py, px+1))
    # South option
    if py < y-1 and currentElevation > grid[py+1][px]:
        options.append((py+1, px))
    # North option
    if py > 0 and currentElevation > grid[py-1][px]:
        options.append((py-1, px))
    return options

def dp_dfs(px, py, grid, x, y, record):
    if (py, px) in record:
        return record[(py, px)]
    nextStates = getSuccessorState(px, py, grid, x, y)
    currentElevation = grid[py][px]
    if not nextStates:
        return [[currentElevation]]
    localCopy = []
    for coords in nextStates:
        newy, newx = coords
        if (newy, newx) in record:
            cache = record[(newy, newx)]
            for route in cache:
                newRoute = [currentElevation]
                newRoute.extend(route)
                localCopy.append(newRoute)
            continue
        possibleRoutes = dp_dfs(newx, newy, grid, x, y, record)
        for route in possibleRoutes:
            newRoute = list(route)
            newRoute.insert(0, currentElevation)
            localCopy.append(newRoute)
    record[(py, px)] = localCopy
    return localCopy

def selectLargestDiff(paths):
    diff = [paths[i][0] - paths[i][-1] for i in range(len(paths))]
    maxDiff = max(diff)
    return [paths[i] for i in range(len(paths)) if diff[i] == maxDiff]

# main function
def main_solution(grid):
    x = len(grid[0])
    y = len(grid)
    record = {}
    possiblePaths = {}
    currentMax = float("-inf")

    for i in range(y):
        for j in range(x):
            temp = dp_dfs(j, i, grid, x, y, record)
            if not temp:
                continue
            for path in temp:
                if not path:
                    continue
                length = len(path)
                if length < currentMax:
                    continue
                currentMax = length
                if length in possiblePaths:
                    possiblePaths[length].append(path)
                else:
                    possiblePaths[length] = [path]

    ans = selectLargestDiff(possiblePaths[currentMax])[0]
    return ans

