    def alphaBeta(self, gameBoard, depth, alpha, beta, isMaxPlayer):
        value = checkIfWon(gameBoard, self.symbol)
        if value != 0:
            return value, -1
        if depth == 0:
            return value, -1
        if isMaxPlayer:
            value = -10
            listOfNumbers = list(range(9))
            while listOfNumbers:
                random = listOfNumbers.pop(randrange(len(listOfNumbers)))
                if gameBoard[random] == '_':
                    gameBoard[random] = self.symbol
                    temp_value = self.alphaBeta(gameBoard, depth - 1, alpha, beta, False)[0]
                    if temp_value > value:
                        value = temp_value
                        place = random
                    alpha = max(alpha, value)
                    gameBoard[random] = '_'
                    if alpha >= beta:
                        break
        else:
            value = 10
            listOfNumbers = list(range(9))
            while listOfNumbers:
                random = listOfNumbers.pop(randrange(len(listOfNumbers)))
                if gameBoard[random] == '_':
                    gameBoard[random] = self.symbol
                    temp_value = self.alphaBeta(gameBoard, depth - 1, alpha, beta, True)[0]
                    if temp_value < value:
                        value = temp_value
                        place = random
                    beta = min(beta, value)
                    gameBoard[random] = '_'
