            gameState[i][j] = symbolToNumber[gameBoard[i * 3 + j]]

    for i in range(3):
        rowcount, colcount = 0, 0
        for j in range(3):
            rowcount += gameState[i][j]
            colcount += gameState[j][i]
        if rowcount == -3 or colcount == -3:
