        return 1
    return k * factorial(k - 1)

# Function to calculate Pi using the Chudnovsky algorithm
def getpi(n):
    getcontext().prec = n + 2  # Set precision to handle n decimal places
    inverse = 0
    for i in range(n + 1):
        num = (-1) ** i * factorial(6 * i) * (545140134 * i + 13591409)
        den = factorial(3 * i) * factorial(i) ** 3 * (640320 ** 3) ** (i + 0.5)
        inverse += 12 * num / den
    pi = Decimal(1 / inverse)
    return round(pi, n)

