# all class and function definitions in the code file, if any
class Node:
    def __init__(self, data):
        self.data = data
        self.left = None
        self.right = None

class BinaryTree:
    def __init__(self):
        self.root = None

    def insert(self, newNode):
        if self.root is None:
            self.root = newNode
        else:
            self.currNode = self.root
            while True:
                if newNode.data < self.currNode.data:
                    if self.currNode.left is not None:
                        self.currNode = self.currNode.left
                    else:
                        self.currNode.left = newNode
                        break
                else:
                    if self.currNode.right is not None:
                        self.currNode = self.currNode.right
                    else:
                        self.currNode.right = newNode
                        break

    def postOrder(self, root):
        result, stack = [], []
        stack.append(root)

        while stack:
            curr = stack.pop()
            result.append(curr.data)

            if curr.left:
                stack.append(curr.left)
            if curr.right:
                stack.append(curr.right)

        result.reverse()
        return result

# main function
def main_solution(preorder):
    # Convert the input list to a binary tree
    tree = BinaryTree()
    for value in preorder:
        node = Node(value)
        tree.insert(node)

    # Get the postorder traversal result
    postorder_result = tree.postOrder(tree.root)

    # Return the postorder traversal result as a list of integers
    return postorder_result

