class TaskScheduler:
    def __init__(self):
        self._task_queue = deque()

    def new_task(self, task):
        self._task_queue.append(task)

    def run(self):
        while self._task_queue:
            task = self._task_queue.popleft()
            try:
                next(task)
                self._task_queue.append(task)
            except StopIteration:
                pass

# main function
def main_solution(countdown_n, countup_n):
    # Convert input variables to the required format
    countdown_n = int(countdown_n)
    countup_n = int(countup_n)

    # Define the generator functions
    def countdown(n):
        while n > 0:
            yield
            n -= 1
        yield 'Blastoff!'

    def countup(n):
        x = 0
        while x < n:
            yield
            x += 1
        yield 'Counting up complete!'

    # Create a TaskScheduler instance
    sched = TaskScheduler()

    # Add tasks to the scheduler
    sched.new_task(countdown(countdown_n))
    sched.new_task(countup(countup_n))

    # Run the scheduler and collect results
    results = []
    while sched._task_queue:
        task = sched._task_queue.popleft()
        try:
            next(task)
            sched._task_queue.append(task)
        except StopIteration as e:
            results.append(str(e))

    # Return the results as a JSON serializable output
    return results

