def mod3plus4(a):
    return a % 4 == 3

def pgcd(a, b):
    while a % b != 0:
        a, b = b, a % b
    return b

def findX(n):
    for i in range(round(n / 2), n):
        if pgcd(i, n) == 1:
            return i
    return -1

def BBS(nb, p, q):
    bits = ""
    if mod3plus4(p) and mod3plus4(q):
        n = p * q
        x = findX(n)
        x0 = xi = x**2 % n
        for i in range(nb):
            xiPlus1 = xi**2 % n
            if xi % 2 == 0:
                bits += "0"
            else:
                bits += "1"
            xi = xiPlus1
    return bits

