    return True, "No one"

def legal_pos(state):
    pos = []
    for i in range(len(state)):
        if state[i] == " ":
            pos.append(i)
    return pos

def sts(state, turn):
    sts = []
    temp = list(state)
    for pos in legal_pos(state):
        temp[pos] = turn
        sts.append(temp)
        temp = list(state)
    return sts

def switch(turn):
    if turn == "X":
        return "O"
    else:
        return "X"

def minimax(S, player, turn):
    if GameOver(S)[0]:
        player = "Player " + player
        if GameOver(S)[1] == player:
            return 1
        elif GameOver(S)[1] == "No one":
            return 0
        else:
            return -1

    if player == turn:
        values = []
        for item in sts(S, turn):
            values.append(minimax(item, player, switch(turn)))
        return max(values)
    else:
        values = []
        for item in sts(S, turn):
            values.append(minimax(item, player, switch(turn)))
        return min(values)

