def get_pairs(pairs_list):
    leftmap = {}
    rightmap = {}
    stack = []
    com = 0
    for pairs in pairs_list:
        for s in pairs:
            if s == "(":
                stack.append(s)
            else:
                if len(stack) == 0:
                    stack.append(s)
                else:
                    if stack[-1] == "(":
                        stack.pop()
                    else:
                        stack.append(s)
        if len(stack) == 0:
            com += 1
            continue

        ln = rn = 0
        while len(stack) > 0:
            top = stack.pop()
            if top == "(":
                ln += 1
            elif top == ")":
                rn += 1

        if ln == 0:
            if rn in rightmap.keys():
                rightmap[rn] += 1
            else:
                rightmap.update({rn: 1})

        if rn == 0:
            if ln in leftmap:
                leftmap[ln] += 1
            else:
                leftmap.update({ln: 1})

    sumpairs = com * com
    for key in leftmap.keys():
        r = rightmap[key] if key in rightmap.keys() else 0
        sumpairs += r * leftmap[key]

    return sumpairs

