# main function
def main_solution(A_preferences, B_preferences):
    # Convert JSON serializable inputs to original input variables
    A = [preference_list(pref) for pref in A_preferences]
    B = [preference_list(pref) for pref in B_preferences]
    n = len(A)

    # the algorithm
    bookmark = [0 for _ in A]
    matchA = {}
    matchB = {}
    matchA[0] = A[0].get(0)
    matchB[matchA[0]] = 0
    bookmark[0] += 1
    i = 1
    while i < n:
        a = i
        while True:
            b = A[a].get(bookmark[a])  # b will be proposed to
            bookmark[a] += 1
            if b not in matchB:  # if b is not engaged...
                matchA[a] = b
                matchB[b] = a
                break
            else:
                a2 = matchB[b]
                # if a is better than current suitor
                if B[b].get_index(a) < B[b].get_index(a2):
                    matchA[a] = b
                    matchB[b] = a
                    matchA.pop(a2)
                    a = a2
        i += 1
    out = [(a, matchA[a]) for a in matchA]
    return out

