# all class and function definitions in the code file, if any
class Program:
    def __init__(self):
        self.rules = {}

    def randomize(self):
        Moves = ('N', 'S', 'W', 'E')
        surrounding = ('xxxx', 'Nxxx', 'NExx', 'NxWx', 'xxxS', 'xExS', 'xxWS', 'xExx', 'xxWx')
        for state in range(5):
            for pattern in surrounding:
                movedir = random.choice(Moves)
                while movedir in pattern:
                    movedir = random.choice(Moves)
                self.rules[(state, pattern)] = (movedir, random.randint(0, 4))

    def getMove(self, state, surrounding):
        return self.rules[(state, surrounding)]

class World:
    def __init__(self, initial_row, initial_col, program):
        self.prow = initial_row
        self.pcol = initial_col
        self.state = 0
        self.prog = program
        self.room = [ [' ']*WIDTH for row in range(HEIGHT) ]
        for col in range(WIDTH):
            self.room[0][col] = '-'
        for col in range(WIDTH):
            self.room[HEIGHT-1][col] = '-' 
        for row in range(HEIGHT):
            self.room[row][0] = '|'
        for row in range(HEIGHT-1):
            self.room[row][WIDTH -1] = '|'
        self.room[0][0] = '+'
        self.room[0][WIDTH-1] = '+'
        self.room[HEIGHT-1][0] = '+'
        self.room[HEIGHT-1][WIDTH-1] = '+'
        self.room[self.prow][self.pcol] = 'P'

    def getCurrentSurroundings(self):
        state = ''
        if self.prow == 1:
            state += 'N'
        else:
            state += 'x'
        if self.pcol == WIDTH - 2:
            state += 'E'
        else:
            state += 'x'
        if self.pcol == 1:
            state += 'W'
        else:
            state += 'x'
        if self.prow == HEIGHT - 2:
            state += 'S'
        else:
            state += 'x'
        return state

    def step(self):
        current_surrounding = self.getCurrentSurroundings()
        NEW = self.prog.getMove(self.state,current_surrounding)
        nextMove = NEW[0]
        nextState = NEW[1]
        self.room[self.prow][self.pcol] = 'o'
        self.state = nextState
        if nextMove == 'N':
            self.prow -= 1
        elif nextMove == 'S':
            self.prow += 1
        elif nextMove == 'W':
            self.pcol -= 1
        else:
            self.pcol += 1
        self.room[self.prow][self.pcol] = 'P'

    def run(self, steps):
        for turn in range(steps):
            self.step()

    def fractionVisitedCells(self):
        cellsVisited = 0
        cellsTotal = 23*23
        for col in range(WIDTH):
            for row in range(HEIGHT):
                if self.room[row][col] == 'o' or self.room[row][col] == 'P':
                    cellsVisited += 1
        return cellsVisited/cellsTotal

def evaluateFitness(program, trials, steps):
    totalFitness = 0
    for x in range(trials):
        row = random.randint(1,23)
        col = random.randint(1,23)
        picobot = World(row, col, program)
        picobot.run(steps)
        totalFitness += picobot.fractionVisitedCells()
    return totalFitness/trials

# main function
def main_solution(initial_row, initial_col, steps, trials):
    # Convert JSON serializable inputs to original input variables
    initial_row = int(initial_row)
    initial_col = int(initial_col)
    steps = int(steps)
    trials = int(trials)

    # Create a random program
    program = Program()
    program.randomize()

    # Evaluate the fitness of the program
    fitness = evaluateFitness(program, trials, steps)

    # Return the fitness as a JSON serializable output
    return {"fitness": fitness}

