def get_neighbors(cell: tuple) -> set:
    dims = len(cell)
    deltas = product(*[[-1, 0, 1]] * dims)
    res = {tuple(c + d for c, d in zip(cell, delta)) for delta in deltas}
    res.remove(cell)
    return res

def next_gen(cells: set) -> set:
    nb = {}
    for c in cells:
        for n in get_neighbors(c):
            try:
                nb[n] += 1
            except KeyError:
                nb[n] = 1
    new_cells = set()
    for c in nb:
        active_neighbors = nb[c]
        if active_neighbors == 3 or (active_neighbors == 2 and c in cells):
            new_cells.add(c)
    return new_cells

def next_gens(cells: set, n: int) -> set:
    for _ in range(n):
        cells = next_gen(cells)
    return cells

