# all class and function definitions in the code file, if any
def create_board(size: int) -> List[List[int]]:
    board = []
    for i in range(size):
        row = []
        for j in range(size):
            row.append(0)
        board.append(row)
    return board

def add_random_queen(chess_board: List[List[int]]) -> List[List[int]]:
    random_indexes = (randint(0, len(chess_board) - 1), randint(0, len(chess_board) - 1))
    if chess_board[random_indexes[0]][random_indexes[1]] == 1:
        return add_random_queen(chess_board)
    chess_board[random_indexes[0]][random_indexes[1]] = 1
    return chess_board

def number_of_queens(board: List[List[int]]) -> int:
    count = 0
    for i in range(len(board)):
        for j in range(len(board[0])):
            if board[i][j] == 1:
                count += 1
    return count

def isOutsideBorder(board: List[List[int]], row: int, col: int) -> bool:
    if row < 0 or row >= len(board):
        return True
    if col < 0 or col >= len(board[0]):
        return True
    return False

def first_queen(board: List[List[int]]) -> List[int]:
    for i in range(len(board)):
        for j in range(len(board[0])):
            if board[i][j] == 1:
                return [i, j]
    return [-1, -1]

def queens(board: List[List[int]]) -> bool:
    if len(board) != 8:
        return False
    for i in range(len(board)):
        if len(board[i]) != 8:
            return False
    if number_of_queens(board) != 2:
        return False

    first_queen_pos = first_queen(board)
    first_queen_row = first_queen_pos[0]
    first_queen_col = first_queen_pos[1]

    for j in range(len(board[first_queen_row])):
        if board[first_queen_row][j] == 1 and j != first_queen_col:
            return True

    for i in range(len(board)):
        if board[i][first_queen_col] == 1 and i != first_queen_row:
            return True

    diagonal_map = [[-1, -1], [-1, +1], [+1, -1], [+1, +1]]
    for i in range(len(diagonal_map)):
        move_row = diagonal_map[i][0]
        move_col = diagonal_map[i][1]
        row = first_queen_row
        col = first_queen_col
        while True:
            row = row + move_row
            col = col + move_col
            if isOutsideBorder(board, row, col):
                break
            if board[row][col] == 1:
                return True

    return False

# main function
def main_solution(board_size: int, queen1_row: int, queen1_col: int, queen2_row: int, queen2_col: int) -> bool:
    # Create the board
    board = create_board(board_size)

    # Place the queens on the board
    board[queen1_row][queen1_col] = 1
    board[queen2_row][queen2_col] = 1

    # Check if the queens can attack each other
    return queens(board)

