def primeFactors2(n, arrayprimos): 
    resta = n
    factores = []
    multi = 1
    i = 0
    while n != 1 and arrayprimos[i] < math.sqrt(n): 
        while n % arrayprimos[i] == 0:
            factores.append(arrayprimos[i])
            n = n / arrayprimos[i]
        i += 1
    # Condition if n is a prime 
    # greater than 2
    if n > 2: 
        factores.append(n)

    frec = collections.Counter(factores)
    for valor in frec:
        multi *= (pow(valor, frec[valor] + 1) - 1) / (valor - 1)
    return int(multi - resta)

def primosff(num, pr):
    div = 0
    while pr[div] < num:
        if num % pr[div] == 0:
            return False
        else:
            if pr[div] > math.sqrt(num):
                return True
            else:
                div += 1
    return True

def generate_primes(limit):
    prim = 3
    arrayprimos = [2, 3]
    while prim < math.sqrt(limit):
        prim += 2
        if primosff(prim, arrayprimos):
            arrayprimos.append(prim)
    return arrayprimos

# main function
def main_solution(limit):
    # Generate prime numbers up to the square root of the limit
    arrayprimos = generate_primes(limit)

    # Find amicable numbers
    amicable_numbers = []
    for x in range(220, limit):
        if x % 2 == 0 or x % 5 == 0:
            di = primeFactors2(x, arrayprimos)
            if di > x and di <= limit:
                dj = primeFactors2(di, arrayprimos)
                if dj == x:
                    amicable_numbers.append((x, di))

    # Return the list of amicable numbers
    return amicable_numbers

