# all class and function definitions in the code file, if any
def vector_distance(vector1, vector2):
    if len(vector1) != len(vector2):
        raise ValueError('These two vectors do not have the same length.')

    d = 0
    for i in range(len(vector1)):
        d = (vector1[i] - vector2[i])**2 + d
    d = math.sqrt(d)
    return d

def normalize(vector1):
    length = 0
    for value in vector1:
        length = length + value**2
    length = math.sqrt(length)
    if length > 0:
        for i in range(len(vector1)):
            vector1[i] = vector1[i]/length
    return vector1      

def clean_word(word):
    word = word.strip('.')
    word = word.strip(',')
    word = word.strip('!')
    word = word.strip('?')
    return word

# main function
def main_solution(userstring, listofarticles):
    # Convert listofarticles to a list of strings
    listofarticles = listofarticles.split(',')

    # Break the string into a list of words
    userstring = userstring.lower()
    parsedstring = userstring.split()

    # Collect the unique words in parsedstring in a dictionary
    masterword_dictionary = {}
    for word in parsedstring:
        masterword_dictionary[word] = 0

    # Make a list of only the unique words in the search string
    masterword_list = list(masterword_dictionary.keys())

    # Initialize the word matrix
    wordmatrix = []

    # Loop through list of article filenames, open the article file, read the content of the article
    for article_title in listofarticles:
        word_dictionary = {}
        words = []
        wordvec = []

        # Simulate reading the article content
        article_words = article_title.lower().split()

        # Clean and collect words
        for w in article_words:
            w = clean_word(w)
            words.append(w)

        # Count words that are in the search string
        for w in words:
            if w in masterword_dictionary.keys():
                word_dictionary[w] = word_dictionary.get(w, 0) + 1

        # Update masterword_dictionary with article counts
        for w in masterword_dictionary.keys():
            if w in word_dictionary.keys():
                masterword_dictionary[w] = masterword_dictionary.get(w, 0) + 1

        # Create word vector for the article
        for word in masterword_list:
            if word in word_dictionary.keys():
                wordvec.append(word_dictionary[word])
            else:
                wordvec.append(0)

        # Normalize the word vector
        wordvec = normalize(wordvec)
        wordmatrix.append(wordvec)

    # Calculate Inverse Document Frequency (IDF)
    numarticles = len(listofarticles)
    InverseDocumentFreq = {}
    for w in masterword_dictionary.keys():
        if masterword_dictionary[w] > 0:
            InverseDocumentFreq[w] = math.log(numarticles / masterword_dictionary[w])
        else:
            InverseDocumentFreq[w] = 1

    # Weight the word counts using IDF
    for row in range(len(wordmatrix)):
        for col in range(len(wordmatrix[row])):
            word_col = masterword_list[col]
            wordmatrix[row][col] = wordmatrix[row][col] * InverseDocumentFreq[word_col]
        wordmatrix[row] = normalize(wordmatrix[row])

    # Create the masterword vector
    masterword_vec = []
    for word in masterword_list:
        masterword_vec.append(masterword_dictionary[word] * InverseDocumentFreq[word])
    masterword_vec = normalize(masterword_vec)

    # Calculate distances between masterword_vec and rows in wordmatrix
    distance = []
    for i in range(numarticles):
        distance.append(vector_distance(masterword_vec, wordmatrix[i][:]))

    # Find the closest article(s)
    closest_article_score = min(distance)
    closest_articles = [listofarticles[index] for index, score in enumerate(distance) if score == closest_article_score]

    # Return the closest articles
    return {"closest_articles": closest_articles}

