    results = []
    for i in range(start, end + 1, 2):
        results.append(str(i))
        for j in range(2, i // 2 + 1):
            if is_prime(j) and is_prime(i - j):
                results.append("=")
                results.append(str(j))
                results.append("+")
                results.append(str(i - j))
        results = [' {0} '.format(elem) for elem in results]
