from collections import namedtuple
import random

# all class and function definitions in the code file, if any
Point = namedtuple('Point', 'x y')

class Line:
    def __init__(self, start, end):
        self.start = Point(*start)
        self.end = Point(*end)
        if self.start.x == self.end.x:
            step = 1 if self.end.y > self.start.y else -1
            self.points = [
                Point(self.start.x, y) for y in
                range(self.start.y, self.end.y + step, step)
            ]
        else:
            step = 1 if self.end.x > self.start.x else -1
            self.points = [
                Point(x, self.start.y) for x in
                range(self.start.x, self.end.x + step, step)
            ]
        self.steps = len(self.points)

    def intersect(self, line):
        p = set(self.points) & set(line.points)
        if p:
            return p.pop()
        raise ValueError('Lines do not intersect.')

def manhattan(point):
    return abs(point.x) + abs(point.y)

def collect_lines(wire):
    pos = [0, 0]
    lines = []
    for item in wire:
        start = pos[:]
        direction = item[0]
        steps = int(item[1:])
        if direction == 'R':
            pos[0] += steps
        elif direction == 'L':
            pos[0] -= steps
        elif direction == 'U':
            pos[1] += steps
        elif direction == 'D':
            pos[1] -= steps
        else:
            raise ValueError(f'Unknown direction: {direction}')
        lines.append(Line(start, pos[:]))
    return lines

# main function
def main_solution(wire_1, wire_2):
    # Convert JSON serializable inputs to original input variables
    wire_1 = wire_1.split(',')
    wire_2 = wire_2.split(',')

    # Collect lines for both wires
    lines_1 = collect_lines(wire_1)
    lines_2 = collect_lines(wire_2)

    # Find intersection points
    points = []
    for line in lines_1[1:]:
        for l in lines_2[1:]:
            try:
                point = line.intersect(l)
                points.append(point)
            except ValueError:
                pass

    # Calculate Manhattan distances and find the minimum
    if points:
        min_distance = min(map(manhattan, points))
    else:
        min_distance = None

    # Return the minimum distance as JSON serializable output
    return min_distance

