import math

# all class and function definitions in the code file, if any
class Tuple(tuple):
    def __new__(self, x, y, z, w):
        return tuple.__new__(Tuple, (x, y, z, w))

    @staticmethod
    def Point(x, y, z):
        return Tuple(x, y, z, 1.0)

    @staticmethod
    def Vector(x, y, z):
        return Tuple(x, y, z, 0.0)

    @staticmethod
    def is_point(t):
        return isinstance(t, Tuple) and len(t) == 4 and t[3] == 1.0

    @staticmethod
    def is_vector(t):
        return isinstance(t, Tuple) and len(t) == 4 and t[3] == 0.0

    def __add__(self, other):
        return Tuple(self.x + other.x, self.y + other.y, self.z + other.z, self.w + other.w)

    def __sub__(self, other):
        return Tuple(self.x - other.x, self.y - other.y, self.z - other.z, self.w - other.w)

    def __neg__(self):
        return Tuple(-self.x, -self.y, -self.z, -self.w)

    def __mul__(self, other):
        return Tuple(self.x * other, self.y * other, self.z * other, self.w * other)

    def __truediv__(self, other):
        return Tuple(self.x / other, self.y / other, self.z / other, self.w / other)

    def __div__(self, other):
        return Tuple(self.x / other, self.y / other, self.z / other, self.w / other)

    def mag(self):
        return math.sqrt(sum(_ * _ for _ in self[:3]))

    def norm(self):
        return self / self.mag()

    def dot(self, other):
        return sum([a * b for a, b in zip(self, other)])

    def cross(self, other):
        assert self.w == 0 and other.w == 0
        a, b = self, other
        return Tuple(
            a.y * b.z - a.z * b.y,
            a.z * b.x - a.x * b.z,
            a.x * b.y - a.y * b.x,
            0.0,
            )

    @property
    def x(self):
        return self[0]

    @property
    def y(self):
        return self[1]

    @property
    def z(self):
        return self[2]

    @property
    def w(self):
        return self[3]

    def reflect(self, normal):
        return self - normal * 2 * self.dot(normal)

# create aliases so we don't need to prefix with Tuple everywhere
Point = Tuple.Point
Vector = Tuple.Vector

# main function
def main_solution(point_coords, vector_coords, normal_coords):
    # Convert JSON serializable inputs to original input variables
    point = Point(*point_coords)
    vector = Vector(*vector_coords)
    normal = Vector(*normal_coords)

    # Perform the reflection operation
    reflected_vector = vector.reflect(normal)

    # Convert the result to JSON serializable output
    reflected_vector_coords = (reflected_vector.x, reflected_vector.y, reflected_vector.z)

    return reflected_vector_coords

