def getDimList(X):
    return [(w, X-w+1) for w in range(1, (X+1)//2+1)]

def WorstBlockWillCutBoardInHalf(X, R, C):
    a = min(R, C)
    b = max(R, C)
    dims = getDimList(X)
    if a >= 2 and any([a == min(d) for d in dims]):
        return True
    else:
        return False

def worstBlockWillFit(X, R, C):
    a = min(R, C)
    b = max(R, C)
    if all([x <= a and y <= b for (x, y) in getDimList(X)]):
        return True
    else:
        return False

# main function
def main_solution(X, R, C):
    # all input arguments of the main_solution function should be json serializable (no self-defined objects, functions, np.array, set, tuple, etc.)
    if X >= 7:
        return "RICHARD"

    if R * C % X != 0:
        return "RICHARD"

    if X in [1, 2]:
        return "GABRIEL"

    if X == 3:
        if min(R, C) == 1:
            return "RICHARD"
        else:
            return "GABRIEL"

    if worstBlockWillFit(X, R, C):
        if WorstBlockWillCutBoardInHalf(X, R, C):
            return "RICHARD"
        else:
            return "GABRIEL"
    else:
        return "RICHARD"

