class Node:
    def __init__(self, data):
        self.parent = self
        self.rank = 0
        self.data = data

def make_set(x):
    """
    创建集合
    :param x:
    :return:
    """
    node = Node(data=x)
    return node

def find_set(node):
    if node != node.parent:
        node.parent = find_set(node.parent) # 路径压缩
    return node

def _link(node1, node2):
    if not node1 or not node2:
        return node1 if node1 else node2

    if node1.rank >= node2.rank:
        node2.parent = node1
        if node1.rank == node2.rank:
            node1.rank += 1
        return node1
    else:
        node1.parent = node2
        return node2

def union_set(node1, node2):
    _link(find_set(node1), find_set(node2))

