import math

# all class and function definitions in the code file, if any
class Triad:
    """
    Calculate angles and distances among a triad of points.
    """
    def __init__(self, p, q, r):
        self.p = p
        self.q = q
        self.r = r

    def d2(self, a, b):
        return float(sum((ia - ib) * (ia - ib) for ia, ib in zip(a, b)))

    def dot(self, a, b):
        return float(sum(ia * ib for ia, ib in zip(a, b)))

    def ndot(self, a, b, c):
        return float(sum((ia - ib) * (ic - ib) for ia, ib, ic in zip(a, b, c)))

    def dPQ(self):
        return math.sqrt(self.d2(self.p, self.q))

    def dQR(self):
        return math.sqrt(self.d2(self.q, self.r))

    def angleQ(self):
        return math.acos(self.ndot(self.p, self.q, self.r) / math.sqrt(self.d2(self.p, self.q) * self.d2(self.r, self.q)))

# main function
def main_solution(C, N, Ca):
    """
    Calculates the N-C bond length, N-Ca bond length, and C-N-Ca bond angle given the 3D coordinates of C, N, and Ca.

    Input:
        C (list of float): Coordinates of Carbon atom in 3D space.
        N (list of float): Coordinates of Nitrogen atom in 3D space.
        Ca (list of float): Coordinates of Calcium atom in 3D space.

    Output:
        dict: A dictionary containing the bond lengths and angle.
              Keys: 'N-C bond length', 'N-Ca bond length', 'C-N-Ca bond angle'
    """
    triadPoints = Triad(p=C, q=N, r=Ca)
    nToC = triadPoints.dPQ()
    nToCa = triadPoints.dQR()
    bondAngle = math.degrees(triadPoints.angleQ())

    return {
        'N-C bond length': round(nToC, 2),
        'N-Ca bond length': round(nToCa, 2),
        'C-N-Ca bond angle': round(bondAngle, 1)
    }

