def integrand(theta, m, x):
    return np.cos(m * theta - x * np.sin(theta))

def trapezium(a, b, m, x):
    area = 0
    h = (b - a) / 10000
    for n in range(0, 10000):
        area += h * ((integrand(h * n, m, x) + integrand(h * (n + 1), m, x)) / 2)
    return area

def J(m, x):
    return 1 / np.pi * trapezium(0, np.pi, m, x)

# main function
