    # Simulate time calculation for a passenger
    return sum(coordinates) * random.uniform(0.8, 1.2)

def timeOfDriver(coordinates):
    # Simulate time calculation for a driver
    return sum(coordinates) * random.uniform(0.8, 1.2)

def getMidPoint(origin, destination):
    # Calculate the midpoint between two coordinates
    return [(origin[0] + destination[0]) / 2, (origin[1] + destination[1]) / 2]

