    midpointOfOriginalRoute = getMidPoint(origin, destination)

    if abs(timeOfPassenger(midpointOfOriginalRoute) - timeOfDriver(midpointOfOriginalRoute)) < 0.1:
        return midpointOfOriginalRoute

    if timeOfPassenger(midpointOfOriginalRoute) < timeOfDriver(midpointOfOriginalRoute):
        return BinarySearch(midpointOfOriginalRoute, destination)
    else:
        return BinarySearch(origin, midpointOfOriginalRoute)

