    for c in range(7):
        for r in range(3):
            if board[c][r]==board[c][r+1]==board[c][r+2]==board[c][r+3]!='_':
                w += transform(board[c][r])*100000
    # horizontally:
    for c in range(4):
        for r in range(6):
            if board[c][r]==board[c+1][r]==board[c+2][r]==board[c+3][r]!='_':
                w += transform(board[c][r])*100000
    # / diagonally:
    for c in range(4):
        for r in range(3):
            if board[c][r]==board[c+1][r+1]==board[c+2][r+2]==board[c+3][r+3]!='_':
                w += transform(board[c][r])*100000
    # \ diagonally:
    for c in range(3,7):
        for r in range(3):
            if board[c][r]==board[c-1][r+1]==board[c-2][r+2]==board[c-3][r+3]!='_':
                w += transform(board[c][r])*100000
