            move(a, 'O', copy)
            if evaluate(copy) > 90000:
                pos_moves[evaluate(copy)] = a
            else:
                listb = []
                for b in range(1,8):
                    if copy[b-1][5] == '_':
                        move(b, 'X', copy)
                        if evaluate(copy) > 90000:
                            listb.append(evaluate(copy))
                        else:
                            listc = []
                            for c in range(1,8):
                                if board[c-1][5] == '_':
                                    move(c, 'O', copy)
                                    if evaluate(copy) > 90000:
