                                    else:
                                        listd = []
                                        for d in range(1,8):
                                            if board[d-1][5] == '_':
                                                move(d, 'X', copy)
                                                listd.append(evaluate(copy))
                                                move(d, '_', copy)
                                        if listd != []:
                                            listc.append(evaluate(copy))
                                    move(c, '_', copy)
                            if listc != []:
                                listb.append(max(listc))
                        move(b, '_', copy)
                if listb != []:
                    pos_moves[min(listb)] = a
            move(a, '_', board)
    if pos_moves == {}:
        return None
    else:
        return pos_moves[max(pos_moves)]

