class Graph:

    def __init__(self, vertices):
        self.V = vertices
        self.graph = defaultdict(list)

    def addEdge(self, u, v):
        self.graph[u].append(v)
        self.graph[v].append(u)

    def remEdge(self, u, v):
        for i in self.graph:
            if i == u:
                self.graph[i].remove(v)
        for i in self.graph:
            if i == v:
                self.graph[i].remove(u)

    def DFS(self, v, visited):
        visited[v] = True
        for i in self.graph[v]:
            if visited[i] == False:
                self.DFS(i, visited)

    def isConnected(self):
        visited = defaultdict(bool)
        for i in self.graph:
            visited[i] = False
        counter = 0
        for i in self.graph:
            counter += 1
            if len(self.graph[i]) > 1:
                break
        if counter == self.V - 1:
            return True
        self.DFS(i, visited)
        for i in self.graph:
            if visited[i] == False and len(self.graph[i]) > 0:
                return False
        return True

    def isEulerianCircuitExists(self):
        if self.isConnected() == False:
            return False
        else:
            odd = 0
            for i in self.graph:
                if len(self.graph[i]) % 2 != 0:
                    odd += 1
            if odd == 0:
                return True
            else: return False

    def EulerianCircuitfromHeirholzer(self):
        stack = []
        circuit = []
        start_vertex = list(self.graph.keys())[0]
        stack.append(start_vertex)
        while stack:
            v = stack[-1]
            if len(self.graph[v])==0:
                circuit.append(stack.pop())
            else:
                next_vertex = self.graph[v][0]
                self.remEdge(v,next_vertex)
                stack.append(next_vertex)
        return circuit

# main function
def main_solution(vertices, edges):
    # Convert JSON serializable inputs to original input variables
    graph = Graph(vertices)
    for edge in edges:
        graph.addEdge(edge[0], edge[1])

    # Check if Eulerian Circuit exists
    if graph.isEulerianCircuitExists():
        circuit = graph.EulerianCircuitfromHeirholzer()
        # Convert the output to JSON serializable format
        return json.dumps(circuit)
    else:
        return json.dumps([])

