# all class and function definitions in the code file, if any
class Polynomial:
    """ Class for work with polynomials.
        Coefficients are saved as a list attribute (coeffs). """

    def __init__(self, *args, **kwargs):
        """ Saves coefficients of polynomial set as arguments to a self.coeffs list """
        self.coeffs = []
        if args:
            for elem in args:
                if isinstance(elem, list):
                    self.coeffs = elem
                    break
                else:
                    self.coeffs.append(elem)
        if kwargs:
            iterator = 0
            for key, val in sorted(kwargs.items()):
                key = int(key[1:])
                while key != iterator:
                    self.coeffs.append(0)
                    iterator = iterator + 1
                self.coeffs.append(val)
                iterator = iterator + 1
        for elem in self.coeffs:
            if (self.coeffs[-1] == 0) and (len(self.coeffs) != 1):
                self.coeffs.pop()
            else:
                break

    def __str__(self):
        """ Conversion polynomial to a string """
        if all(i == 0 for i in self.coeffs):
            return "0"
        string = ""
        cnt = 0
        for elem in self.coeffs:
            if elem == 0:
                cnt = cnt + 1
                continue
            elif (elem == 1 or elem == -1) and cnt != 0:
                coeff = ""
            else:
                coeff = str(abs(elem))
            if cnt == len(self.coeffs) - 1:
                if elem < 0:
                    sign = "- "
                else:
                    sign = ""
            elif elem < 0:
                sign = " - "
            else:
                sign = " + "
            if cnt == 0:
                power = ""
            elif cnt == 1:
                power = "x"
            else:
                power = "x^" + str(cnt)
            string = sign + coeff + power + string
            cnt = cnt + 1
        return string

    def __eq__(self, other):
        """ Compare two polynomials """
        return self.coeffs == other.coeffs

    def __add__(self, other):
        """ Returns sum of polynomials """
        A = self.coeffs
        B = other.coeffs
        result = list()
        if len(A) > len(B):
            for i, b in enumerate(B):
                result.append(A[i] + b)
            i += 1
            while i < len(A):
                result.append(A[i])
                i += 1
        else:
            for i, a in enumerate(A):
                result.append(B[i] + a)
            i += 1
            while i < len(B):
                result.append(B[i])
                i += 1
        return str(Polynomial(result))

    def __pow__(self, exp):
        """ Returns polynomial power to the exp """
        power = int(exp)
        result = []
        for k in range(0, power + 1):
            nCr = math.factorial(power) // math.factorial(k) // math.factorial(power - k)
            result.append(nCr * ((self.coeffs[1]) ** (power - k)) * (self.coeffs[0] ** k))
        return Polynomial(result[::-1])

    def derivative(self):
        """ Returns derived polynomial """
        derivated = []
        idx = 1;
        for elem in self.coeffs[1:]:
            derivated.append(elem * idx)
            idx += 1
        return Polynomial(derivated)

    def at_value(self, *x):
        """ Returns value of polynomial at x,
            when there are 2 values, it returns their difference """
        x = list(x)
        if len(x) == 1:
            idx = 0
            result = 0
            for elem in self.coeffs:
                result += (x[0] ** idx) * elem
                idx += 1
            return result
        elif len(x) == 2:
            if x[0] > x[1]:
                return self.at_value(x[0]) - self.at_value(x[1])
            else:
                return self.at_value(x[1]) - self.at_value(x[0])

# main function
def main_solution(coefficients, x_values):
    """
    Computes the value of a polynomial at given x values.

    Parameters:
    coefficients (list of int): The coefficients of the polynomial in descending order of powers.
    x_values (list of float): The x values at which the polynomial is to be evaluated.

    Returns:
    dict: A dictionary with keys as the x values and values as the corresponding polynomial values.
    """
    polynomial = Polynomial(coefficients)
    result = {}
    for x in x_values:
        result[x] = polynomial.at_value(x)
    return result

