import numpy as np

# all class and function definitions in the code file, if any
# they will be used in the main_solution function, you may need to modify them to meet the requirements of the main_solution function (optional)
class Ray:
    def __init__(self, orig, dir):
        self.orig = np.array(orig)
        self.dir = np.array(dir)

class Sphere:
    def __init__(self, pos, radius=1.0):
        self.pos = np.array(pos)
        self.radius = radius

    def intersect(self, ray):
        omc = ray.orig - self.pos
        dir_omc = np.dot(ray.dir, omc)
        omc_omc = np.dot(omc, omc)
        dis = dir_omc**2 - omc_omc + self.radius**2
        if dis < 0: return 0.0
        t = -dir_omc - np.sqrt(dis)
        if t > 0.0: return t
        t = -dir_omc + np.sqrt(dis)
        return t if t > 0.0 else 0.0

    def normal(self, point):
        return normalize(point - self.pos)

def normalize(vec):
    return vec / np.linalg.norm(vec, ord=2)

# main function
def main_solution(sphere_pos, sphere_radius, ray_orig, ray_dir):
    # all input arguments of the main_solution function should be json serializable (no self-defined objects, functions, np.array, set, tuple, etc.)
    sphere = Sphere(sphere_pos, sphere_radius)
    ray = Ray(ray_orig, ray_dir)

    # Calculate the intersection distance
    intersection_distance = sphere.intersect(ray)

    # Calculate the normal at the intersection point
    if intersection_distance > 0.0:
        intersection_point = ray.orig + intersection_distance * ray.dir
        normal_at_intersection = sphere.normal(intersection_point)
    else:
        normal_at_intersection = None

    # return, the returned value must be json serializable (no self-defined objects, functions, np.array, set, tuple, etc.)
    return {
        "intersection_distance": intersection_distance,
        "normal_at_intersection": normal_at_intersection.tolist() if normal_at_intersection is not None else None
    }

