# all class and function definitions in the code file, if any
class Ship:
    def __init__(self, length: int, row: int, col: int, orient: int):
        self.length = length
        self.row = row
        self.col = col
        self.orient = orient
        self.hits = 0
        self.sunk = False

    def hit(self):
        self.hits += 1
        if self.hits == self.length:
            self.sunk = True

class Human:
    def __init__(self):
        self._my_ships: List[Ship] = []
        self._my_misses: List[Tuple[int, int]] = []
        self._my_hits: List[Tuple[int, int]] = []
        self._sunk_ships: List[Ship] = []
        self._their_misses: List[Tuple[int, int]] = []
        self._their_hits: List[Tuple[int, int]] = []
        self._board_matrix: List[List[Optional[Ship]]] = [[None] * 10 for _ in range(10)]
        self.complete: bool = False

    def initialize(self):
        for ship_length in [5, 4, 3, 3, 2]:
            validPlac = False
            while not validPlac:
                orient = random.randint(0, 1)
                if orient == 1:
                    row = random.randint(0, 10 - ship_length)
                    col = random.randint(0, 9)
                else:
                    row = random.randint(0, 9)
                    col = random.randint(0, 10 - ship_length)

                my_ship = Ship(ship_length, row, col, orient)

                conflict = ['No']
                if orient == 1:
                    for n in range(ship_length):
                        if self._board_matrix[row + n][col] is None:
                            conflict.append('No')
                        else:
                            conflict.append('Yes')
                else:
                    for n in range(ship_length):
                        if self._board_matrix[row][col + n] is None:
                            conflict.append('No')
                        else:
                            conflict.append('Yes')

                if 'Yes' not in conflict:
                    for n in range(ship_length):
                        if orient == 1:
                            self._board_matrix[row + n][col] = my_ship
                        else:
                            self._board_matrix[row][col + n] = my_ship

                    self._my_ships.append(my_ship)
                    validPlac = True

    def guess(self, row: int, col: int) -> Tuple[int, Optional[Ship]]:
        my_ship: Ship = self._board_matrix[row][col]

        if my_ship is not None and (row, col) not in self._their_hits:
            self._their_hits.append((row, col))
            my_ship.hit()
            if my_ship.sunk:
                return (2, my_ship)
            else:
                return (1, None)
        else:
            self._their_misses.append((row, col))
            return (0, None)

# main function
def main_solution(guesses: List[Tuple[int, int]]) -> List[Tuple[int, Optional[int]]]:
    human = Human()
    human.initialize()

    results = []
    for guess in guesses:
        result = human.guess(*guess)
        results.append((result[0], result[1].length if result[1] else None))

    return results

