# import necessary packages
import re
import collections

# Tokenizer 分词器
NUM = "(?P<NUM>\d+)"
PLUS = "(?P<PLUS>\+)"
MINUS = "(?P<MINUS>\-)"
TIMES = "(?P<TIMES>\*)"
DIVIDE = "(?P<DIVIDE>/)"
LPAREN = "(?P<LPAREN>\()"
RPAREN = "(?P<RPAREN>\))"
WS = "?<WS>\s+"

master_pat = re.compile('|'.join([NUM, PLUS, MINUS, TIMES, DIVIDE, LPAREN, RPAREN]))
Token = collections.namedtuple("Token", ['type', 'value'])

def generate_tokens(text):
    scanner = master_pat.scanner(text)
    for m in iter(scanner.match, None):
        tok = Token(m.lastgroup, m.group())
        if tok.type != 'WS':
            yield tok

# parser 语法分析器
class ExpressionEvaluator:
    def parse(self, text):
        self.tokens = generate_tokens(text)
        self.tok = None  # Last symbol consumed
        self.nexttok = None  # Next symbol tokenized
        self._advance()  # Load first lookahead token
        return self.expr()

    def _advance(self):
        'Advance one token ahead'
        self.tok, self.nexttok = self.nexttok, next(self.tokens, None)

    def _accept(self, toktype):
        'Test and consume the next token if it matches toktype'
        if self.nexttok and self.nexttok.type == toktype:
            self._advance()
            return True
        else:
            return False

    def _except(self, toktype):
        'Consume next token if it matches toktype or raise SyntaxError'
        if not self._accept(toktype):
            raise SyntaxError('Expected ' + toktype)

    def expr(self):
        "expr ::= term {('+'|'-') term}*"
        exprval = self.term()
        while self._accept('PLUS') or self._accept('MINUS'):
            op = self.tok.type
            right = self.term()
            if op == 'PLUS':
                exprval += right
            elif op == 'MINUS':
                exprval -= right
        return exprval

    def term(self):
        "term ::= factor { ('*'|'/') factor }*"
        termval = self.factor()
        while self._accept('TIMES') or self._accept('DIVIDE'):
            op = self.tok.type
            right = self.factor()
            if op == 'TIMES':
                termval *= right
            elif op == 'DIVIDE':
                termval /= right
        return termval

    def factor(self):
        "factor ::= NUM | ( expr )"
        if self._accept('NUM'):
            return int(self.tok.value)
        elif self._accept('LPAREN'):
            exprval = self.expr()
            self._except('RPAREN')
            return exprval
        else:
            raise SyntaxError('Expected NUMBER OR LPAREN')

# main function
def main_solution(expression):
    """
    Evaluates a mathematical expression using a recursive descent parser.

    Parameters:
    expression (str): A string representing a mathematical expression to be evaluated.

    Returns:
    int: The result of the evaluated expression.
    """
    evaluator = ExpressionEvaluator()
    return evaluator.parse(expression)

