import random

# all class and function definitions in the code file, if any
def generateRandomDNASeq(length):
    nucleotides = ['A', 'G', 'C', 'T']
    sequence = ''
    for i in range(length):
        sequence += random.choice(nucleotides)
    return sequence

def kimura2Parameter(sequence, a, b):
    mutationMatrix = {'A':[1-a-(2*b), b, a, a],
                      'C':[b, 1-a-(2*b), b, a],
                      'G':[a, b, 1-a-(2*b), b],
                      'T':[a, a, b, 1-a-(2*b)]}
    matNucleoOrder = ['A', 'C', 'G', 'T']

    for i in range(len(sequence)):
        noMutation = True
        probsCount = 0
        randomNum = 0
        while noMutation and probsCount < 3:
            randomNum = random.random()
            if randomNum <= mutationMatrix[sequence[i]][probsCount]:
                noMutation = False
            probsCount += 1

        if not(noMutation):
            sequence = sequence[:i] + matNucleoOrder[probsCount-1]+ sequence[i+1:]

    return sequence

# main function
def main_solution(N, L, G):
    originalSeq = generateRandomDNASeq(L)
    population = []
    for i in range(N):
        population.append(originalSeq)

    for i in range(G):
        for j in range(len(population)):
            newJ = kimura2Parameter(population[j], 0.3, 0.1)
            population[j] = newJ

    # Convert the population list to a JSON serializable format
    population_str = ','.join(population)
    return {"original_sequence": originalSeq, "population": population_str}

