import random

# all class and function definitions in the code file, if any
class Equation:
    def __init__(self, a, b):
        self.a = a
        self.b = b

class Position:
    def __init__(self, column, row):
        self.column = column
        self.row = row

def SelectPivotElement(a, used_rows, used_columns):
    pivot_element = Position(0, 0)
    while used_rows[pivot_element.row]:
        pivot_element.row += 1
    while used_columns[pivot_element.column]:
        pivot_element.column += 1
    return pivot_element

def SwapLines(a, b, used_rows, pivot_element):
    pe = pivot_element
    if a[pe.row][pe.column] == 0:
        for i in range(pe.row + 1, len(a)):
            if a[i][pe.column] != 0:
                pivot_element.row = i
                break
    a[pivot_element.column], a[pivot_element.row] = a[pivot_element.row], a[pivot_element.column]
    b[pivot_element.column], b[pivot_element.row] = b[pivot_element.row], b[pivot_element.column]
    used_rows[pivot_element.column], used_rows[pivot_element.row] = used_rows[pivot_element.row], used_rows[pivot_element.column]
    pivot_element.row = pivot_element.column
    return (a, b)

def scale(a, b, pivot_element):
    pe = pivot_element
    peval = a[pe.row][pe.column]
    if peval == 1:
        return (a, b)
    for i in range(len(a[pe.row])):
        a[pe.row][i] /= peval
    b[pe.row] /= peval
    return (a, b)

def zero(a, b, pivot_element):
    pe = pivot_element
    peval = a[pe.row][pe.column]
    if peval != 1:
        return (a, b)
    for i in range(pe.row + 1, len(a)):
        val = a[i][pe.column]
        x = 0 - val
        for j in range(pe.column, len(a)):
            a[i][j] += a[pe.row][j] * x
        b[i] += b[pe.row] * x
    return (a, b)

def ProcessPivotElement(a, b, pivot_element):
    (a, b) = scale(a, b, pivot_element)
    (a, b) = zero(a, b, pivot_element)

def MarkPivotElementUsed(pivot_element, used_rows, used_columns):
    used_rows[pivot_element.row] = True
    used_columns[pivot_element.column] = True

def SolveEquation(equation):
    a = equation.a
    b = equation.b
    size = len(a)
    used_columns = [False] * size
    used_rows = [False] * size
    for step in range(size):
        pivot_element = SelectPivotElement(a, used_rows, used_columns)
        (a, b) = SwapLines(a, b, used_rows, pivot_element)
        ProcessPivotElement(a, b, pivot_element)
        MarkPivotElementUsed(pivot_element, used_rows, used_columns)
    for i in reversed(range(size - 1)):
        leftsum = 0
        for j in range(i + 1, len(a)):
            leftsum += a[i][j] * b[j]
        b[i] -= leftsum
    return b

# main function
def main_solution(matrix_a, vector_b):
    # Convert JSON serializable inputs to original input variables
    equation = Equation(matrix_a, vector_b)
    # Solve the equation
    solution = SolveEquation(equation)
    # Convert the solution to JSON serializable output
    return solution

