    key = int(list(alphabet.keys())[list(alphabet.values()).index(m)])

    e = g**d % p

    ggTCheck = 0

    while ggTCheck != 1:
        r = randint(1, p-1)
        ggTCheck = ggT(r, p-1)

    mir = ExtGCD(p-1, r)
    if mir < 0:
