def gcd(a, b):
    while b != 0:
        a, b = b, a % b
    return a

def get_d(e, z):
    a = e
    b = z
    x = 0
    y = 1
    q = 0
    d = 0

    while True:
        if a == 1:
            return y
        if a == 0:
            return -1
        q = b // a
        b = b - a * q
        x = x + q * y
        if b == 1:
            d = z - x
            break
        if b == 0:
            return -1
        q = a // b
        a = a - b * q
        y = y + q * x
    while d < 0:
        d += z
    return d

def is_prime(num):
    if num > 1:
        for i in range(2, num // 2):
            if (num % i) == 0:
                return False
        return True
    else:
        return False

def generate_keypair(p, q):
    if not (is_prime(p) and is_prime(q)):
        raise ValueError('Both numbers must be prime.')
    elif p == q:
        raise ValueError('p and q cannot be equal')
    n = p * q
    z = (p - 1) * (q - 1)
    e = random.randrange(1, z)

    while gcd(e, z) != 1:
        e = random.randrange(1, z)

    d = get_d(e, z)
    return ((e, n), (d, n))

def encryptRSA(pk, plaintext):
    AsciiVal = ord(plaintext)
    cipher = pow(AsciiVal, pk[0], pk[1])
    return cipher

def decryptRSA(pk, ciphertext):
    AsciiVal = pow(ciphertext, pk[0], pk[1])
    plain = chr(AsciiVal)
    return plain

