    q = int(q)
    plaintext = str(plaintext)

    # generate RSA keypair
    public_key, private_key = generate_keypair(p, q)

    # encrypt the plaintext
    ciphertext = encryptRSA(public_key, plaintext)

    # decrypt the ciphertext
    decrypted_text = decryptRSA(private_key, ciphertext)

    # return the final output, which must be json serializable
    return {"ciphertext": ciphertext, "decrypted_text": decrypted_text}

