    def power(x, y, p):
        res = 1
        x = x % p
        while y > 0:
            if (y & 1) == 1:
                res = (res * x) % p
            y = y >> 1
            x = (x * x) % p
        return res

    # List to store primitive roots
    primitive_roots = []

