def derivative(f, x):
    return (f(x - x / 10000) + f(x + x / 10000)) / (2 * x / 10000)

def integral(f, a, b):
    summation = 0
    for i in range(10000):
        summation += (b - a) / 10000 * f(a + i * (b - a) / 10000)
    return summation

def grad(f, v):
    return [
        derivative(lambda x: f(x, v[1], v[2]), v[0]),
        derivative(lambda y: f(v[0], y, v[2]), v[1]),
        derivative(lambda z: f(v[0], v[1], z), v[2])
    ]

# main function
def main_solution(function_str, operation, point):
    # Convert the function_str to a callable function
    f = eval(f"lambda x: {function_str}")

    if operation == "derivative":
        result = derivative(f, point)
    elif operation == "integral":
        result = integral(f, point[0], point[1])
    elif operation == "gradient":
        result = grad(lambda x, y, z: eval(function_str), point)
    else:
        raise ValueError("Invalid operation")

    return result

