            smaller = r_y
        else:
            larger = r_y
            smaller = r_x

        self.s[larger] += self.s[smaller]
        self.s[smaller] = larger

        return -self.s[larger]

class Maze:

    ch = 'X'

    def __init__(self, rows, cols):
        self.rows = rows
        self.cols = cols        
        self.generate()

    def generate(self):

        rows = self.rows
        cols = self.cols

        total = rows * cols

        d = DisjointSets(total)
        right = [True] * total
        bot = [True] * total

        right[-1] = False

        walls = total*2-rows-cols
        per_row = (cols-1)*2 + 1
        last_row = cols-1

        choices = list(range(walls))
        random.shuffle(choices)

        while d.count() > 1:
            x = choices.pop()

            if x >= walls-last_row:
                row = rows-1
                col = x - (walls-last_row)
                cell = row*cols + col
                if d.union(cell, cell+1) != -1:
                    right[cell] = False
            else:
                row = x // per_row
                col = (x % per_row) // 2
                dir = (x % per_row) % 2
                cell = row*cols + col
                if col == cols-1 or dir == 0:
                    if d.union(cell, cell+cols) != -1:
                        bot[cell] = False
                else:
                    if d.union(cell, cell+1) != -1:
                        right[cell] = False

        self.right = right
        self.bot = bot

    def __repr__(self):

        s = ''

        # top wall
        s += Maze.ch * (self.cols * 2 + 1) + '\n'

        for row in range(self.rows):
            s += (' ' if row == 0 else Maze.ch)
            for col in range(self.cols):
                s += ' '
                s += (Maze.ch if self.right[row*self.cols + col] else ' ')
            s += '\n' + Maze.ch
            for col in range(self.cols):
                s += ((Maze.ch * 2) if self.bot[row*self.cols + col] else (' ' + Maze.ch))
            s += '\n'

        return s

# main function
def main_solution(rows, cols):
    # Convert input to the required format for the Maze class
    maze_rows = rows // 2
    maze_cols = cols // 2

    # Generate the maze
    maze = Maze(maze_rows, maze_cols)

    # Convert the maze representation to a JSON serializable format
    maze_repr = repr(maze)

    # Return the maze representation
    return maze_repr

